import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { subscriptionService } from "./subscriptionServices";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, async () => {
    log(`serving on port ${port}`);

    subscriptionService.updateSubscriptionData().catch((error) => {
      console.error('Failed to update subscription data on startup:', error);
    });

    const ONE_DAY = 24 * 60 * 60 * 1000;
    setInterval(() => {
      subscriptionService.updateSubscriptionData().catch((error) => {
        console.error('Failed to update subscription data:', error);
      });
    }, ONE_DAY);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { igdbService } from "./igdb";
import { insertGameSchema } from "@shared/schema";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { hltbService } from "./hltbService";
import { subscriptionService } from "./subscriptionServices";

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup Replit Auth
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });
  app.post("/api/games/search", async (req, res) => {
    try {
      const { query } = req.body;

      if (!query || typeof query !== 'string') {
        return res.status(400).json({ error: 'Search query is required' });
      }

      const games = await igdbService.searchGames(query, 10);

      const formattedGames = games.map(game => ({
        igdbId: game.id,
        name: game.name,
        coverUrl: igdbService.formatCoverUrl(game.cover?.url),
        releaseDate: game.first_release_date ? new Date(game.first_release_date * 1000) : null,
        platforms: game.platforms?.map(p => p.name) || [],
        metacriticScore: Math.round(game.aggregated_rating || game.rating || 0),
        summary: game.summary || '',
        genres: game.genres?.map(g => g.name) || [],
      }));

      res.json(formattedGames);
    } catch (error) {
      console.error('Search error:', error);
      res.status(500).json({ error: 'Failed to search games' });
    }
  });

  app.post("/api/games", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      const gameData = insertGameSchema.parse({
        ...req.body,
        userId,
      });

      const existingGame = await storage.findUserGameByIgdbId(userId, gameData.igdbId);

      if (existingGame) {
        return res.status(400).json({ error: 'Game already in your collection' });
      }

      const game = await storage.createGame(gameData);
      res.json(game);
    } catch (error) {
      console.error('Add game error:', error);
      res.status(500).json({ error: 'Failed to add game' });
    }
  });

  app.get("/api/games", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const status = req.query.status as string | undefined;

      const games = status 
        ? await storage.getUserGamesByStatus(userId, status)
        : await storage.getUserGames(userId);

      res.json(games);
    } catch (error) {
      console.error('Get games error:', error);
      res.status(500).json({ error: 'Failed to get games' });
    }
  });

  app.patch("/api/games/:id/status", isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const { status } = req.body;

      if (!status || typeof status !== 'string') {
        return res.status(400).json({ error: 'Status is required' });
      }

      const game = await storage.updateGameStatus(id, status);

      if (!game) {
        return res.status(404).json({ error: 'Game not found' });
      }

      res.json(game);
    } catch (error) {
      console.error('Update game error:', error);
      res.status(500).json({ error: 'Failed to update game' });
    }
  });

  app.delete("/api/games/:id", isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteGame(id);

      if (!deleted) {
        return res.status(404).json({ error: 'Game not found' });
      }

      res.json({ success: true });
    } catch (error) {
      console.error('Delete game error:', error);
      res.status(500).json({ error: 'Failed to delete game' });
    }
  });

  app.get("/api/events", async (req, res) => {
    try {
      const events = await igdbService.getUpcomingEvents(50);

      const formattedEvents = events.map(event => ({
        id: event.id,
        name: event.name,
        description: event.description || '',
        startTime: new Date(event.start_time * 1000),
        endTime: event.end_time ? new Date(event.end_time * 1000) : null,
        liveStreamUrl: event.live_stream_url || null,
        logoUrl: igdbService.formatCoverUrl(event.event_logo?.url, 'cover_big'),
        networks: event.event_networks?.map(n => n.name) || [],
        games: event.games?.map(g => g.name) || [],
      }));

      res.json(formattedEvents);
    } catch (error) {
      console.error('Events fetch error:', error);
      res.status(500).json({ error: 'Failed to fetch events' });
    }
  });

  app.get("/api/games/igdb/:igdbId", async (req, res) => {
    try {
      const igdbId = parseInt(req.params.igdbId);

      if (isNaN(igdbId)) {
        return res.status(400).json({ error: 'Invalid game ID' });
      }

      const game = await igdbService.getGameDetails(igdbId);

      if (!game) {
        return res.status(404).json({ error: 'Game not found' });
      }

      const developers = game.involved_companies
        ?.filter(ic => ic.developer)
        .map(ic => ic.company.name) || [];

      const publishers = game.involved_companies
        ?.filter(ic => ic.publisher)
        .map(ic => ic.company.name) || [];

      const formattedGame = {
        igdbId: game.id,
        name: game.name,
        coverUrl: igdbService.formatCoverUrl(game.cover?.url),
        releaseDate: game.first_release_date ? new Date(game.first_release_date * 1000) : null,
        platforms: game.platforms?.map(p => p.name) || [],
        metacriticScore: Math.round(game.aggregated_rating || game.rating || 0),
        summary: game.summary || '',
        storyline: game.storyline || '',
        genres: game.genres?.map(g => g.name) || [],
        developers,
        publishers,
        screenshots: game.screenshots?.map(s => igdbService.formatScreenshotUrl(s.url)) || [],
        videos: game.videos || [],
        gameModes: game.game_modes?.map(m => m.name) || [],
        playerPerspectives: game.player_perspectives?.map(p => p.name) || [],
        themes: game.themes?.map(t => t.name) || [],
        ratingCount: game.aggregated_rating_count || game.rating_count || 0,
      };

      res.json(formattedGame);
    } catch (error) {
      console.error('Get game details error:', error);
      res.status(500).json({ error: 'Failed to get game details' });
    }
  });

  app.get("/api/games/:igdbId/playtime", async (req, res) => {
    try {
      const igdbId = parseInt(req.params.igdbId);

      if (isNaN(igdbId)) {
        return res.status(400).json({ error: 'Invalid game ID' });
      }

      const gameDetails = await igdbService.getGameDetails(igdbId);

      if (!gameDetails) {
        return res.status(404).json({ error: 'Game not found' });
      }

      const playtimeData = await hltbService.getPlaytimeForGame(gameDetails.name);

      await storage.updateGamePlaytime(igdbId, playtimeData.mainStoryHours, playtimeData.completionistHours);

      res.json(playtimeData);
    } catch (error) {
      console.error('Get playtime error:', error);
      res.status(500).json({ error: 'Failed to get playtime data' });
    }
  });

  app.get("/api/games/:igdbId/subscriptions", async (req, res) => {
    try {
      const igdbId = parseInt(req.params.igdbId);

      if (isNaN(igdbId)) {
        return res.status(400).json({ error: 'Invalid game ID' });
      }

      const gameDetails = await igdbService.getGameDetails(igdbId);

      if (!gameDetails) {
        return res.status(404).json({ error: 'Game not found' });
      }

      const subscriptions = await subscriptionService.checkGameSubscriptionsById(igdbId, gameDetails.name);

      res.json(subscriptions);
    } catch (error) {
      console.error('Get subscription error:', error);
      res.status(500).json({ error: 'Failed to get subscription data' });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, jsonb, index, boolean, real } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table for Replit Auth
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export type UpsertUser = typeof users.$inferInsert;
export type User = typeof users.$inferSelect;

export const games = pgTable("games", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  igdbId: integer("igdb_id").notNull(),
  name: text("name").notNull(),
  coverUrl: text("cover_url"),
  releaseDate: timestamp("release_date"),
  platforms: text("platforms").array(),
  platform: text("platform"),
  metacriticScore: integer("metacritic_score"),
  summary: text("summary"),
  genres: text("genres").array(),
  status: text("status").notNull(),
  addedAt: timestamp("added_at").notNull().default(sql`now()`),
  // Subscription service availability
  gamePassConsole: boolean("game_pass_console").default(false),
  gamePassPC: boolean("game_pass_pc").default(false),
  psPlus: boolean("ps_plus").default(false),
  geforceNow: boolean("geforce_now").default(false),
  // HowLongToBeat playtime data (in hours)
  mainStoryHours: real("main_story_hours"),
  completionistHours: real("completionist_hours"),
});

export const insertGameSchema = createInsertSchema(games).omit({
  id: true,
  addedAt: true,
}).extend({
  releaseDate: z.coerce.date().nullable().optional(),
});

export type InsertGame = z.infer<typeof insertGameSchema>;
export type Game = typeof games.$inferSelect;

// Global subscription catalog - tracks which games are on subscription services
export const subscriptionCatalog = pgTable("subscription_catalog", {
  igdbId: integer("igdb_id").primaryKey(),
  name: text("name").notNull(),
  gamePassConsole: boolean("game_pass_console").default(false),
  gamePassPC: boolean("game_pass_pc").default(false),
  psPlus: boolean("ps_plus").default(false),
  geforceNow: boolean("geforce_now").default(false),
  lastUpdated: timestamp("last_updated").notNull().default(sql`now()`),
});

export type SubscriptionCatalogEntry = typeof subscriptionCatalog.$inferSelect;

