import { Gamepad2, CheckCircle, Clock, Heart } from "lucide-react";

interface DashboardStatsProps {
  totalGames: number;
  completedGames: number;
  hoursPlayed: number;
  wishlistCount: number;
}

export function DashboardStats({ 
  totalGames, 
  completedGames, 
  hoursPlayed, 
  wishlistCount 
}: DashboardStatsProps) {
  const stats = [
    {
      title: "Total Games",
      value: totalGames,
      icon: Gamepad2,
      testId: "stat-total-games"
    },
    {
      title: "Completed",
