import { SubscriptionBadges } from "./SubscriptionBadges";
import { Calendar, Clock } from "lucide-react";
import { useLocation } from "wouter";

interface ReleaseGameCardProps {
  id: string;
  title: string;
  coverUrl: string;
  releaseDate: string;
  daysInfo: number;
  isReleased: boolean;
  metacritic?: number;
  platforms: string[];
  igdbId?: number;
  onClick?: () => void;
  gamePassConsole?: boolean;
  gamePassPC?: boolean;
  psPlus?: boolean;
  geforceNow?: boolean;
}

export function ReleaseGameCard({ 
  id, 
  title, 
  coverUrl, 
  releaseDate,
  daysInfo,
  isReleased,
  metacritic, 
  platforms,
  igdbId,
  onClick,
  gamePassConsole,
  gamePassPC,
  psPlus,
  geforceNow
}: ReleaseGameCardProps) {
  const [, setLocation] = useLocation();

  const handleClick = () => {
    if (igdbId) {
      setLocation(`/game/${igdbId}`);
    } else if (onClick) {
      onClick();
    }
  };
  const dayText = isReleased 
    ? `${daysInfo} ${daysInfo === 1 ? 'day' : 'days'} ago`
    : `${daysInfo} ${daysInfo === 1 ? 'day' : 'days'} left`;

  return (
    <Card
