server/subscriptionServices.ts.content:
import { storage } from "./storage";
import { igdbService } from "./igdb";

interface GamePassGame {
  id: string;
  title: string;
  supportedPlatforms?: string[];
}

interface PSPlusGame {
  title: string;
}

interface GeForceNowGame {
  title: string;
}

export class SubscriptionService {
  private gamePassCatalog: GamePassGame[] = [];
  private psPlusCatalog: PSPlusGame[] = [];
  private geforceNowCatalog: GeForceNowGame[] = [];
  private catalogLastUpdated: Date | null = null;
  private initializationPromise: Promise<void> | null = null;
  private subscriptionCache: Map<number, {
    gamePassConsole: boolean;
    gamePassPC: boolean;
    psPlus: boolean;
    geforceNow: boolean;
    cachedAt: Date;
  }> = new Map();
  private async fetchGamePassCatalog(): Promise<GamePassGame[]> {
    try {
      const response = await fetch(
        'https://catalog.gamepass.com/sigls/v2?id=fdd9e2a7-0fee-49f6-ad69-4354098401ff&language=en-us&market=US',
        {
          headers: {
            'Accept': 'application/json',
          },
        }
      );

      if (!response.ok) {
        console.error('Game Pass API error:', response.statusText);
        return [];
      }

      const data = await response.json();
      return data || [];
    } catch (error) {
      console.error('Failed to fetch Game Pass catalog:', error);
      return [];
    }
  }

  private async fetchPSPlusCatalog(): Promise<PSPlusGame[]> {
    try {
      const response = await fetch('https://psdeals.net/ca-store/collection/ps_plus_game_catalog');

      if (!response.ok) {
        console.error('PS+ scrape error:', response.statusText);
        return [];
      }

      const html = await response.text();

      const gameMatches = Array.from(html.matchAll(/(\d+)[a-zA-Z\s]+([^<\n]+)<\/a>/g));
      const games: PSPlusGame[] = [];
      const seenTitles = new Set<string>();

      for (const match of gameMatches) {
        const title = match[2]?.trim();
        if (title && title.length > 2 && !seenTitles.has(title)) {
          seenTitles.add(title);
          games.push({ title });
        }
      }

      console.log(`Found ${games.length} PS+ games`);
      return games;
    } catch (error) {
      console.error('Failed to fetch PS+ catalog:', error);
      return [];
    }
  }

  private async fetchGeForceNowCatalog(): Promise<GeForceNowGame[]> {
    try {
      const response = await fetch('https://www.nvidia.com/en-us/geforce-now/games/');

      if (!response.ok) {
        console.error('GeForce NOW scrape error:', response.statusText);
        return [];
      }

      const html = await response.text();

      const gameMatches = Array.from(html.matchAll(/data-game-name="([^"]+)"/g));
      const games: GeForceNowGame[] = [];
      const seenTitles = new Set<string>();

      for (const match of gameMatches) {
        const title = match[1]?.trim();
        if (title && !seenTitles.has(title)) {
          seenTitles.add(title);
          games.push({ title });
        }
      }

      console.log(`Found ${games.length} GeForce NOW games`);
      return games;
    } catch (error) {
      console.error('Failed to fetch GeForce NOW catalog:', error);
      return [];
    }
  }

  private normalizeTitle(title: string | undefined | null): string {
    if (!title) return '';
    return title
      .toLowerCase()
      .replace(/[^\w\s]/g, '')
      .replace(/\s+/g, ' ')
      .trim();
  }

  private titlesMatch(title1: string, title2: string): boolean {
    const norm1 = this.normalizeTitle(title1);
    const norm2 = this.normalizeTitle(title2);

    if (norm1 === norm2) return true;

    if (norm1.includes(norm2) || norm2.includes(norm1)) {
      const longer = norm1.length > norm2.length ? norm1 : norm2;
      const shorter = norm1.length > norm2.length ? norm2 : norm1;
      if (longer.startsWith(shorter) || longer.endsWith(shorter)) {
        return true;
      }
    }

    return false;
  }

  async initialize(): Promise<void> {
    if (!this.initializationPromise) {
      this.initializationPromise = this.updateSubscriptionData();
    }
    return this.initializationPromise;
  }

  async ensureInitialized(): Promise<void> {
    if (!this.catalogLastUpdated) {
      await this.initialize();
    }
  }

  async updateSubscriptionData(): Promise<void> {
    console.log('Starting subscription catalog update...');
    const startTime = Date.now();

    try {
      const [gamePassGames, psPlusGames, geforceGames] = await Promise.all([
        this.fetchGamePassCatalog(),
        this.fetchPSPlusCatalog(),
        this.fetchGeForceNowCatalog(),
      ]);

      this.gamePassCatalog = gamePassGames;
      this.psPlusCatalog = psPlusGames;
      this.geforceNowCatalog = geforceGames;
      this.catalogLastUpdated = new Date();

      console.log(`Fetched catalogs: Game Pass (${gamePassGames.length}), PS+ (${psPlusGames.length}), GeForce NOW (${geforceGames.length})`);

      const allGames = await storage.getAllGamesForSubscriptionUpdate();
      console.log(`Matching against ${allGames.length} games in database...`);

      let updateCount = 0;
      for (const game of allGames) {
        const subscriptions = this.checkGameSubscriptions(game.name);

        await storage.updateGameSubscriptions(game.igdbId, subscriptions);

        if (subscriptions.gamePassConsole || subscriptions.gamePassPC || subscriptions.psPlus || subscriptions.geforceNow) {
          updateCount++;
        }
      }

      const duration = ((Date.now() - startTime) / 1000).toFixed(2);
      console.log(` Subscription update complete: ${updateCount} games matched in ${duration}s`);
    } catch (error) {
      console.error('Failed to update subscription data:', error);
      this.initializationPromise = null;
      throw error;
    }
  }

  checkGameSubscriptions(gameName: string): {
    gamePassConsole: boolean;
    gamePassPC: boolean;
    psPlus: boolean;
    geforceNow: boolean;
  } {
    const gamePassConsole = this.gamePassCatalog.some(gp => 
      this.titlesMatch(gameName, gp.title) && 
      (!gp.supportedPlatforms || gp.supportedPlatforms.some(p => p?.toLowerCase().includes('console')))
    );

    const gamePassPC = this.gamePassCatalog.some(gp => 
      this.titlesMatch(gameName, gp.title) && 
      (!gp.supportedPlatforms || gp.supportedPlatforms.some(p => p?.toLowerCase().includes('pc')))
    );

    const psPlus = this.psPlusCatalog.some(ps => this.titlesMatch(gameName, ps.title));

    const geforceNow = this.geforceNowCatalog.some(gfn => this.titlesMatch(gameName, gfn.title));

    return {
      gamePassConsole,
      gamePassPC,
      psPlus,
      geforceNow,
    };
  }

  async checkGameSubscriptionsById(igdbId: number, gameName: string): Promise<{
    gamePassConsole: boolean;
    gamePassPC: boolean;
    psPlus: boolean;
    geforceNow: boolean;
  }> {
    await this.ensureInitialized();

    const cached = this.subscriptionCache.get(igdbId);
    if (cached) {
      const cacheAge = Date.now() - cached.cachedAt.getTime();
      if (cacheAge < 24 * 60 * 60 * 1000) {
        return {
          gamePassConsole: cached.gamePassConsole,
          gamePassPC: cached.gamePassPC,
          psPlus: cached.psPlus,
          geforceNow: cached.geforceNow,
        };
      }
    }

    const subscriptions = this.checkGameSubscriptions(gameName);

    this.subscriptionCache.set(igdbId, {
      ...subscriptions,
      cachedAt: new Date(),
    });

    return subscriptions;
  }
}

export const subscriptionService = new SubscriptionService();

client/src/pages/Calendar.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Calendar as CalendarIcon, ExternalLink, Gamepad2, Filter } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { SubscriptionBadges } from "@/components/SubscriptionBadges";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { format } from "date-fns";
import type { Game } from "@shared/schema";
import { useState } from "react";

interface GamingEvent {
  id: number;
  name: string;
  description: string;
  startTime: string;
  endTime: string | null;
  liveStreamUrl: string | null;
  logoUrl: string;
  networks: string[];
  games: string[];
}

export default function Calendar() {
  const [subscriptionFilter, setSubscriptionFilter] = useState<string>("all");

  const { data: events, isLoading: eventsLoading } = useQuery<GamingEvent[]>({
    queryKey: ["/api/events"],
  });

  const { data: userGames, isLoading: gamesLoading } = useQuery<Game[]>({
    queryKey: ["/api/games"],
  });

  const wishlistGames = userGames
    ?.filter(game => {
      if (game.status !== "wishlist" || !game.releaseDate) return false;

      if (subscriptionFilter === "all") return true;
      if (subscriptionFilter === "gamepass") return game.gamePassConsole || game.gamePassPC;
      if (subscriptionFilter === "psplus") return game.psPlus;
      if (subscriptionFilter === "geforce") return game.geforceNow;

      return true;
    })
    .sort((a, b) => {
      const dateA = a.releaseDate ? new Date(a.releaseDate).getTime() : 0;
      const dateB = b.releaseDate ? new Date(b.releaseDate).getTime() : 0;
      return dateA - dateB;
    }) || [];

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-outfit font-semibold flex items-center gap-2" data-testid="text-calendar-title">
          <CalendarIcon className="h-8 w-8 text-primary" />
          Calendar
        </h1>
        <p className="text-muted-foreground mt-1">
          Track gaming events and upcoming releases from your wishlist
        </p>
      </div>

      <Tabs defaultValue="events" className="w-full">
        <TabsList>
          <TabsTrigger value="events" data-testid="tab-events">Gaming Events</TabsTrigger>
          <TabsTrigger value="wishlist" data-testid="tab-wishlist">Wishlist Releases</TabsTrigger>
        </TabsList>

        <TabsContent value="events" className="mt-6">
          {eventsLoading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
              <p className="text-muted-foreground">Loading gaming events...</p>
            </div>
          ) : !events || events.length === 0 ? (
            <div className="text-center py-12">
              <CalendarIcon className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
              <p className="text-lg text-muted-foreground mb-2">No upcoming gaming events</p>
              <p className="text-sm text-muted-foreground">
                Check back later for major gaming announcements
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {events.map((event) => (
                <Card key={event.id} className="hover-elevate" data-testid={`card-event-${event.id}`}>
                  {event.logoUrl && (
                    <div className="relative h-40 w-full overflow-hidden rounded-t-md">
                      <img
                        src={event.logoUrl}
                        alt={event.name}
                        className="w-full h-full object-cover"
                      />
                    </div>
                  )}
                  <CardHeader>
                    <CardTitle className="text-lg font-outfit" data-testid={`text-event-name-${event.id}`}>
                      {event.name}
                    </CardTitle>
                    <div className="text-sm text-muted-foreground" data-testid={`text-event-date-${event.id}`}>
                      {format(new Date(event.startTime), 'MMMM d, yyyy  h:mm a')}
                    </div>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    {event.description && (
                      <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`text-event-desc-${event.id}`}>
                        {event.description}
                      </p>
                    )}
                    {event.networks && event.networks.length > 0 && (
                      <div className="flex flex-wrap gap-1">
                        {event.networks.slice(0, 3).map((network, idx) => (
                          <Badge key={idx} variant="secondary" className="text-xs">
                            {network}
                          </Badge>
                        ))}
                      </div>
                    )}
                    {event.games && event.games.length > 0 && (
                      <div className="text-xs text-muted-foreground">
                        <Gamepad2 className="h-3 w-3 inline mr-1" />
                        {event.games.length} game{event.games.length !== 1 ? 's' : ''}
                      </div>
                    )}
                    {event.liveStreamUrl && (
                      <Button 
                        variant="outline" 
                        size="sm" 
                        className="w-full"
                        onClick={() => window.open(event.liveStreamUrl!, '_blank')}
                        data-testid={`button-livestream-${event.id}`}
                      >
                        <ExternalLink className="h-3 w-3 mr-2" />
                        Watch Livestream
                      </Button>
                    )}
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="wishlist" className="mt-6">
          <div className="mb-4 flex items-center gap-3">
            <Filter className="h-4 w-4 text-muted-foreground" />
            <Select value={subscriptionFilter} onValueChange={setSubscriptionFilter}>
              <SelectTrigger className="w-[200px]" data-testid="select-subscription-filter">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Games</SelectItem>
                <SelectItem value="gamepass">Game Pass</SelectItem>
                <SelectItem value="psplus">PlayStation Plus</SelectItem>
                <SelectItem value="geforce">GeForce NOW</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {gamesLoading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
              <p className="text-muted-foreground">Loading wishlist...</p>
            </div>
          ) : wishlistGames.length === 0 ? (
            <div className="text-center py-12">
              <CalendarIcon className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
              <p className="text-lg text-muted-foreground mb-2">No upcoming releases in your wishlist</p>
              <p className="text-sm text-muted-foreground">
                Add games to your wishlist to track their release dates
              </p>
            </div>
          ) : (
            <div className="space-y-3">
              {wishlistGames.map((game) => (
                <Card key={game.id} className="hover-elevate" data-testid={`card-wishlist-game-${game.id}`}>
                  <CardContent className="flex items-center gap-4 p-4">
                    <img
                      src={game.coverUrl || 'https://via.placeholder.com/100x140?text=No+Cover'}
                      alt={game.name}
                      className="w-20 h-28 object-cover rounded-md flex-shrink-0"
                      data-testid={`img-game-cover-${game.id}`}
                    />
                    <div className="flex-1 min-w-0">
                      <h3 className="font-outfit font-semibold text-lg mb-1 truncate" data-testid={`text-game-name-${game.id}`}>
                        {game.name}
                      </h3>
                      {game.releaseDate && (
                        <div className="flex items-center gap-2 text-sm text-muted-foreground mb-2">
                          <CalendarIcon className="h-4 w-4" />
                          <span data-testid={`text-release-date-${game.id}`}>
                            {format(new Date(game.releaseDate), 'MMMM d, yyyy')}
                          </span>
                        </div>
                      )}
                      <div className="space-y-2">
                        {game.platform && (
                          <Badge variant="secondary" className="text-xs">
                            {game.platform}
                          </Badge>
                        )}
                        <SubscriptionBadges 
                          gamePassConsole={game.gamePassConsole || undefined}
                          gamePassPC={game.gamePassPC || undefined}
                          psPlus={game.psPlus || undefined}
                          geforceNow={game.geforceNow || undefined}
                        />
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

server/hltbService.ts.content:
import { HowLongToBeatService, HowLongToBeatEntry } from 'howlongtobeat';

export interface PlaytimeData {
  mainStoryHours: number | null;
  completionistHours: number | null;
}

class HLTBService {
  private hltb: HowLongToBeatService;

  constructor() {
    this.hltb = new HowLongToBeatService();
  }

  async getPlaytimeForGame(gameName: string): Promise<PlaytimeData> {
    try {
      const results = await this.hltb.search(gameName);

      if (!results || results.length === 0) {
        return { mainStoryHours: null, completionistHours: null };
      }

      const bestMatch = results.reduce((best, current) => 
        current.similarity > best.similarity ? current : best
      );

      if (bestMatch.similarity < 0.4) {
        console.log(`Low similarity match for "${gameName}": ${bestMatch.similarity}`);
        return { mainStoryHours: null, completionistHours: null };
      }

      return {
        mainStoryHours: bestMatch.gameplayMain || null,
        completionistHours: bestMatch.gameplayCompletionist || null,
      };
    } catch (error) {
      console.error(`Failed to fetch HLTB data for "${gameName}":`, error);
      return { mainStoryHours: null, completionistHours: null };
    }
  }
}

export const hltbService = new HLTBService();

server/replitAuth.ts.content:
// Replit Auth integration using OpenID Connect
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";
import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: true,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  // Keep track of registered strategies
  const registeredStrategies = new Set<string>();

  // Helper function to ensure strategy exists for a domain
  const ensureStrategy = (domain: string) => {
    const strategyName = `replitauth:${domain}`;
    if (!registeredStrategies.has(strategyName)) {
      const strategy = new Strategy(
        {
          name: strategyName,
          config,
          scope: "openid email profile offline_access",
          callbackURL: `https://${domain}/api/callback`,
        },
        verify,
      );
      passport.use(strategy);
      registeredStrategies.add(strategyName);
    }
  };

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    ensureStrategy(req.hostname);
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    ensureStrategy(req.hostname);
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

client/src/components/SubscriptionBadges.content:
import { Gamepad2 } from "lucide-react";
import { Badge } from "@/components/ui/badge";

interface SubscriptionBadgesProps {
  gamePassConsole?: boolean;
  gamePassPC?: boolean;
  psPlus?: boolean;
  geforceNow?: boolean;
}

export function SubscriptionBadges({ gamePassConsole, gamePassPC, psPlus, geforceNow }: SubscriptionBadgesProps) {
  const badges = [];

  if (gamePassConsole) {
    badges.push(
      <Badge key="gp-console" variant="secondary" className="gap-1" data-testid="badge-gamepass-console">
        <Gamepad2 className="h-3 w-3" />
        <span className="text-xs">Game Pass</span>
      </Badge>
    );
  }

  if (gamePassPC) {
    badges.push(
      <Badge key="gp-pc" variant="secondary" className="gap-1" data-testid="badge-gamepass-pc">
        <Gamepad2 className="h-3 w-3" />
        <span className="text-xs">PC Game Pass</span>
      </Badge>
    );
  }

  if (psPlus) {
    badges.push(
      <Badge key="ps-plus" variant="secondary" className="gap-1" data-testid="badge-psplus">
        <Gamepad2 className="h-3 w-3" />
        <span className="text-xs">PS Plus</span>
      </Badge>
    );
  }

  if (geforceNow) {
    badges.push(
      <Badge key="gfn" variant="secondary" className="gap-1" data-testid="badge-geforce-now">
        <Gamepad2 className="h-3 w-3" />
        <span className="text-xs">GeForce NOW</span>
      </Badge>
    );
  }

  if (badges.length === 0) {
    return null;
  }

  return <div className="flex flex-wrap gap-1">{badges}</div>;
}
